class Warehouse {
    int stock;
    boolean isOpen;

    public Warehouse(int initialStock) {
        stock = initialStock;
        isOpen = false;
    }

    public void open() {
        if (stock > 0) {
            isOpen = true;
        }
    }

    public void receiveShipment(int items) {
        stock += items;
        if (stock > 0 && !isOpen) {
            open();
        }
    }

    public boolean canSellItems(int quantity) {
        if (isOpen && stock >= quantity) {
            stock -= quantity;
            return true;
        }
        return false;
    }
