class WeatherSimulator {
  int temperature;
  bool isRaining;
  bool hasUmbrella;

  WeatherSimulator(int temp, bool rain) {
    temperature = temp;
    isRaining = rain;
    hasUmbrella = false;
  }

  void takeUmbrella() {
    hasUmbrella = true;
  }

  bool willGoOutside() {
    if (isRaining && !hasUmbrella) {
      return false;
    } else {
      return true;
    }
  }

  bool willFeelCold() {
    if (temperature < 15 && !isRaining) {
      return true;
    } else {
      return false;
    }
  }
}",

