class TemperatureController:
    def __init__(self, threshold):
        self.threshold = threshold
        self.temperature = 25  # Initial temperature in degrees Celsius

    def increase_temperature(self, increment):
        self.temperature += increment
        if self.temperature > self.threshold:
            self.power_state = 'ON'
        else:
            self.power_state = 'OFF'

    def decrease_temperature(self, decrement):
        self.temperature -= decrement

    def check_power_state(self):
        return self.power_state

controller = TemperatureController(30)
controller.increase_temperature(10)
controller.decrease_temperature(5)
