import random

class SalesSimulator:
    def __init__(self, base_price=100, holiday=False):
        self.base_price = base_price
        self.holiday = holiday
        self.demand_multiplier = self.calculate_demand_multiplier()

    def calculate_demand_multiplier(self):
        if self.holiday:
            return random.uniform(1.5, 2.0)
        else:
            return random.uniform(0.8, 1.2)

    def simulate_sales(self, discount):
        price = self.base_price * (1 - discount)
        return price * random.uniform(self.demand_multiplier, self.demand_multiplier * 1.2)
