class HealthSystem {
    int age;
    boolean smoker;
    double healthRiskScore;

    HealthSystem(int age, boolean smoker) {
        this.age = age;
        this.smoker = smoker;
        calculateRisk();
    }

    void calculateRisk() {
        healthRiskScore = age * 1.5;
        if (smoker) {
            healthRiskScore += 50;
        }
    }

    boolean isHighRisk() {
        return healthRiskScore > 100;
    }
}",

