class PollutionController:
    def __init__(self, industry_emissions, car_emissions):
        self.industry_emissions = industry_emissions
        self.car_emissions = car_emissions

    def total_emissions(self):
        return self.industry_emissions + self.car_emissions

    def update_car_emissions(self, new_emissions):
        self.car_emissions = new_emissions
        return self.car_emissions

    def policy_impact(self, reduction_factor):
        reduced_industry = self.industry_emissions * reduction_factor
        reduced_cars = self.car_emissions * reduction_factor
        return reduced_industry + reduced_cars",

