class WeatherSystem {
    private boolean isRaining = false;
    private int temperature = 25;
    private boolean usesIrrigation = false;

    public void triggerRain() {
        if (!isRaining && temperature < 20) {
            isRaining = true;
        }
    }

    public void setTemperature(int temp) {
        temperature = temp;
        if (temperature >= 30) {
            isRaining = false;
        }
    }

    public void setIrrigation(boolean status) {
        usesIrrigation = status;
        if (usesIrrigation) {
            temperature += 5;
        } else {
            temperature -= 5;
        }
    }

    public boolean isItRaining() {
        return isRaining;
    }

    public int getTemperature() {
        return temperature;
    }
  }
