class TemperatureController:
    def __init__(self, temp):
        self.desired_temp = temp
        self.current_temp = temp
        self.heater_on = False

    def update_temperature(self, external_temp):
        if self.heater_on:
            self.current_temp += 1
        else:
            self.current_temp = external_temp

    def check_temperature(self):
        if self.current_temp < self.desired_temp:
            self.heater_on = True
        elif self.current_temp > self.desired_temp:
            self.heater_on = False

    def set_desired_temperature(self, new_temp):
        self.desired_temp = new_temp

# Example usage:
controller = TemperatureController(22)
controller.update_temperature(18)
controller.check_temperature()",

