import random

class Weather:
    def __init__(self):
        self.is_raining = bool(random.randint(0, 1))

class Plant:
    def __init__(self, growth_rate):
        self.size = 1
        self.growth_rate = growth_rate

    def grow(self, weather):
        if weather.is_raining:
            self.size += self.growth_rate

class Herbivore:
    def __init__(self):
        self.energy = 50

    def eat(self, plant):
        if plant.size > 10:
            self.energy += 20
            plant.size -= 10

    def digest(self):
        self.energy -= 10

def simulate_day(weather, plant, herbivore):
        plant.grow(weather)
        herbivore.digest()
        herbivore.eat(plant)

# Creating instances of Weather, Plant, and Herbivore
weather = Weather()
plant = Plant(growth_rate=5)
herbivore = Herbivore()

# Simulating a day in the ecosystem
simulate_day(weather, plant, herbivore)
