    class SmartThermostat {
      constructor(initialSetting) {
        this.internalTemp = initialSetting;
        this.externalTemp = 0;
        this.energyUsage = 0;
        this.bill = 0;
      }

      updateExternalTemp(newTemp) {
        this.externalTemp = newTemp;
        this.adjustThermostat();
      }

      userSetting(newSetting) {
        this.internalTemp = newSetting;
        this.adjustThermostat();
      }

      adjustThermostat() {
        if (this.externalTemp < 0) {
          this.energyUsage += (this.internalTemp - this.externalTemp) * 1.5;
        } else {
          this.energyUsage += (this.internalTemp - this.externalTemp);
        }
        this.calculateBill();
      }

      calculateBill() {
        this.bill = this.energyUsage * 0.05;
      }

      getBillingInfo() {
        return this.bill;
      }
    }

    // Example usage:
    let homeThermostat = new SmartThermostat(20); // setting internal temp to 20 degrees
    homeThermostat.updateExternalTemp(-5); // colder outside, more heating needed
    `,
    "variables": {
