class HealthApp {
    private int steps;
    private boolean active;

    public HealthApp() {
        this.steps = 0;
        this.active = false;
    }

    public void recordSteps(int count) {
        steps += count;
        if (steps > 10000) {
            active = true;
        }
    }

    public boolean isActive() {
        return active;
    }

    public void reset() {
        steps = 0;
        active = false;
    }
}",

