class WeatherControlSystem:
    def __init__(self, current_temp, desired_temp):
        self.current_temp = current_temp
        self.desired_temp = desired_temp
        self.heater = False
        self.aircon = False

    def system_update(self):
        temp_difference = self.desired_temp - self.current_temp
        if temp_difference > 0:
            self.heater = True
            self.aircon = False
        elif temp_difference < 0:
            self.heater = False
            self.aircon = True
        else:
            self.heater = False
            self.aircon = False

    def get_status(self):
        return {'current_temp': self.current_temp, 'desired_temp': self.desired_temp, 'heater_on': self.heater, 'aircon_on': self.aircon}
