class BookStore {
  constructor() {
    this.stock = [];
    this.sales = 0;
  }

  addBook(book) {
    if (!this.stock.includes(book)) {
      this.stock.push(book);
      console.log('Book added.');
    } else {
      console.log('Book already in stock.');
    }
  }

  sellBook(title) {
    let index = this.stock.findIndex(book => book.title === title);
    if (index !== -1) {
      this.stock.splice(index, 1);
      this.sales += 1;
      console.log('Book sold.');
    } else {
      console.log('Book not found.');
    }
  }
