class Market:
    def __init__(self, supply, demand):
        self.supply = supply  # Number of products available
        self.demand = demand  # Number of products desired by customers

    def calculate_price(self):
        if self.supply < self.demand:
            price = 1.2 * self.demand / self.supply  # Higher demand than supply leads to higher prices
        else:
            price = self.supply / self.demand  # Higher supply than demand reduces prices
        return price

