class InventorySystem:
    def __init__(self, initial_stock):
        self.stock = initial_stock
        self.purchase_history = []

    def purchase_item(self, item_id, quantity):
        if item_id in self.stock:
            self.stock[item_id] -= quantity
            self.purchase_history.append((item_id, quantity))

    def restock_item(self, item_id, quantity):
        if item_id in self.stock:
            self.stock[item_id] += quantity
        else:
            self.stock[item_id] = quantity

    def get_stock_level(self, item_id):
        return self.stock.get(item_id, 0)

    def get_recent_purchase(self):
