    class SalesAnalysis:
        def __init__(self, data):
            self.sales_data = data

        def total_sales(self, year):
            return sum(sale['amount'] for sale in self.sales_data if sale['year'] == year)

        def apply_discount(self, discount_rate):
            for sale in self.sales_data:
                sale['amount'] -= sale['amount'] * discount_rate

        def predict_future_sales(self, growth_rate, years):
            current_year = max(sale['year'] for sale in self.sales_data)
            last_year_sales = self.total_sales(current_year)
            return last_year_sales * ((1 + growth_rate) ** years)

    # Example usage:
    data = [
        {'year': 2020, 'amount': 120},
        {'year': 2021, 'amount': 150},
        {'year': 2022, 'amount': 180}
    ]
    analysis = SalesAnalysis(data)
    analysis.apply_discount(0.1)
    future_sales = analysis.predict_future_sales(0.05, 2)
