class ShoppingCart:
    def __init__(self):
        self.items = []
        self.total = 0

    def add_item(self, item_price):
        if item_price > 0:
            self.items.append(item_price)
            self.total += item_price

    def apply_discount(self, discount):
        if discount > 0 and discount < 1:
            self.total *= (1 - discount)

    def remove_item(self, item_price):
        if item_price in self.items:
            self.items.remove(item_price)
            self.total -= item_price",

