    class WeatherSimulator:
        def __init__(self):
            self.temperature = 20  # degrees Celsius
            self.humidity = 50  # percent

        def increase_temperature(self, increment):
            if increment > 0:
                self.temperature += increment
                self.adjust_humidity(-0.1 * increment)

        def increase_humidity(self, increment):
            if increment > 0:
                self.humidity += increment

        def adjust_humidity(self, change):
            new_humidity = self.humidity + change
            if new_humidity < 20:
                self.humidity = 20
            elif new_humidity > 80:
                self.humidity = 80
            else:
                self.humidity = new_humidity

        def report_weather(self):
            return f"Temperature: {self.temperature}, Humidity: {self.humidity}"

    # Example of using the WeatherSimulator
    simulator = WeatherSimulator()
    simulator.increase_temperature(5)
    simulator.increase_humidity(10)
    print(simulator.report_weather())
