class ShoppingCart {
  int itemCount;
  double totalPrice;

  ShoppingCart() {
    itemCount = 0;
    totalPrice = 0.0;
  }

  void addItem(double price) {
    itemCount++;
    totalPrice += price;
  }

  void applyDiscount(double discountRate) {
    totalPrice *= (1 - discountRate);
  }

  void removeItem(double price) {
    if (itemCount > 0) {
      itemCount--;
      totalPrice -= price;
    }
  }
}",

