    def __init__(self, initial_population):
        self.population = initial_population
        self.health_status = {'healthy': initial_population - 10, 'sick': 10}

    def spread_virus(self, interactions):
        new_cases = interactions * 0.1
        if new_cases < self.health_status['healthy']:
            self.health_status['sick'] += new_cases
            self.health_status['healthy'] -= new_cases

    def distribute_medicine(self, doses):
        treated_cases = min(doses, self.health_status['sick'])
        self.health_status['sick'] -= treated_cases
        self.health_status['healthy'] += treated_cases
