class HealthMonitor:
    def __init__(self, age, weight, smoker):
        self.age = age
        self.weight = weight
        self.smoker = smoker
        self.health_score = 100
        self.evaluate_health()

    def evaluate_health(self):
        if self.age > 50:
            self.health_score -= 10
        if self.weight > 200:
            self.health_score -= 20
        if self.smoker:
            self.health_score -= 30
        return self.health_score


