class ClimateController {
  private int temperature;
  private int humidity;
  private boolean airConditionerOn;

  public ClimateController(int initialTemp, int initialHumidity) {
    temperature = initialTemp;
    humidity = initialHumidity;
    airConditionerOn = false;
  }

  public void increaseTemperature() {
    if (!airConditionerOn) {
      temperature += 3;
    }
  }

  public void decreaseTemperature() {
    temperature -= 2;
    if (temperature <= 25) {
      airConditionerOn = false;
    }
  }

  public void toggleAirConditioner() {
    airConditionerOn = !airConditionerOn;
  }

  public void setHumidity(int newHumidity) {
    humidity = newHumidity;
  }

  public int getTemperature() {
    return temperature;
  }

  public boolean isAirConditionerOn() {
    return airConditionerOn;
  }
}",

