class HealthMonitor:
    def __init__(self):
        self.heart_rate = 70
        self.blood_pressure = (120, 80)
        self.exercise_level = 0

    def update_exercise_level(self, minutes):
        self.exercise_level += minutes
        self.adjust_heart_rate()
        self.adjust_blood_pressure()

    def adjust_heart_rate(self):
        if self.exercise_level < 30:
            self.heart_rate = 70 + self.exercise_level * 2
        else:
            self.heart_rate = 130 + (self.exercise_level - 30) * 3

    def adjust_blood_pressure(self):
        if self.exercise_level > 50:
            increase = (self.exercise_level - 50) * 2
            self.blood_pressure = (120 + increase, 80 + increase // 2)
        else:
            self.blood_pressure = (120, 80)

    def exercise_intervention(self, minutes):
        original_level = self.exercise_level
        self.update_exercise_level(minutes)
        if self.exercise_level - original_level > 40:
            self.heart_rate -= 15  # counteract excessive increase for safety
            self.blood_pressure = (self.blood_pressure[0] - 10, self.blood_pressure[1] - 5)

    def get_health_report(self):
        return {'Heart Rate': self.heart_rate, 'Blood Pressure': self.blood_pressure}
