        self.items = []
        self.total_cost = 0

    def add_item(self, item, price):
        self.items.append(item)
        self.total_cost += price

    def remove_item(self, item, price):
        if item in self.items:
            self.items.remove(item)
            self.total_cost -= price
        else:
