    class HealthMonitor:
        def __init__(self, age, weight, heart_rate):
            self.age = age
            self.weight = weight
            self.heart_rate = heart_rate

        def risk_factor(self):
            bmi = self.weight / (self.height * self.height)
            if self.heart_rate > 100:
                return 'High'
            elif bmi > 25:
                self.heart_rate += 5
                return 'Medium'
            else:
                return 'Low'

        def update_metrics(self, new_weight, new_heart_rate):
            self.weight = new_weight
            self.heart_rate = new_heart_rate
            if self.weight > 200:
                self.heart_rate += 10
            return self.risk_factor()

    def main():
        person = HealthMonitor(30, 180, 80)
        print(person.risk_factor())
        print(person.update_metrics(205, 85))

    if __name__ == '__main__':
        main()
