class DataProcessor:
    def __init__(self, data):
        self.data = data
        self.cleaned_data = None
        self.statistics = {}

    def clean_data(self):
        self.cleaned_data = [x for x in self.data if x >= 0]  # remove negative values
        return self.cleaned_data

    def compute_statistics(self):
        if self.cleaned_data is None:
            self.clean_data()
        self.statistics['mean'] = sum(self.cleaned_data) / len(self.cleaned_data)
        self.statistics['max'] = max(self.cleaned_data)
        self.statistics['min'] = min(self.cleaned_data)

    def report_statistics(self):
        if not self.statistics:
            self.compute_statistics()
        return self.statistics

