class Ecosystem:
    def __init__(self, sunlight, water, soil_quality):
        self.sunlight = sunlight
        self.water = water
        self.soil_quality = soil_quality
        self.plant_growth = 0

    def simulate_day(self):
        if self.water > 50 and self.sunlight > 75:
            self.plant_growth += self.soil_quality * 0.1
        elif self.water < 30 or self.sunlight < 30:
            self.plant_growth -= 5
        else:
            self.plant_growth += 2

