  class Product:
      def __init__(self, name, price, quantity):
          self.name = name
          self.price = price
          self.quantity = quantity

  class Inventory:
      def __init__(self):
          self.items = {}

      def add_product(self, product, quantity):
          if product.name in self.items:
              self.items[product.name].quantity += quantity
          else:
              self.items[product.name] = product
              self.items[product.name].quantity = quantity

      def sell_product(self, product_name, quantity):
          if product_name in self.items and self.items[product_name].quantity >= quantity:
              self.items[product_name].quantity -= quantity
              return True
          return False

      def check_stock(self, product_name):
          if product_name in self.items:
              return self.items[product_name].quantity
          return 0

      def total_value(self):
          return sum(product.price * product.quantity for product in self.items.values())

