class CoffeeShop:
    def __init__(self):
        self.inventory = {'coffee': 100, 'milk': 100, 'sugar': 100}
        self.staff = 3
        self.sales = 0

    def sell_coffee(self, cups):
        if self.inventory['coffee'] >= cups and self.inventory['milk'] >= cups:
            self.inventory['coffee'] -= cups
            self.inventory['milk'] -= cups
            self.sales += cups * 5
            return True
        return False

    def restock(self, item, amount):
        if self.staff > 1 and item in self.inventory:
            self.inventory[item] += amount
            return True
        return False

    def adjust_staff(self, number):
        self.staff += number
        if self.staff < 1:
            self.staff = 1
        elif self.staff > 10:
            self.staff = 10

