# A Python script to calculate bonus based on sales and attendance
class Employee:
    def __init__(self, id, name, sales, attendance_rate):
        self.id = id
        self.name = name
        self.sales = sales
        self.attendance_rate = attendance_rate
        self.bonus = 0

    def calculate_bonus(self):
        if self.sales > 50000:
            if self.attendance_rate >= 0.95:
                self.bonus = 1000
            else:
                self.bonus = 500
        elif self.sales > 20000:
            if self.attendance_rate >= 0.90:
                self.bonus = 500
            else:
                self.bonus = 200
        else:
            self.bonus = 0

    def display(self):
        print(f"Employee ID: {self.id}, Name: {self.name}, Bonus: {self.bonus}")

