class WeatherSimulator {
  private boolean isRaining;
  private int temperature;

  public WeatherSimulator(boolean rain, int temp) {
    this.isRaining = rain;
    this.temperature = temp;
  }

  public void updateWeather(int newTemperature) {
    if (newTemperature > 30) {
      isRaining = false;
    } else if (newTemperature < 10) {
      isRaining = true;
    }
