  class Store:
      def __init__(self, inventory):
          self.inventory = inventory

      def sell(self, item):
          if item in self.inventory and self.inventory[item] > 0:
              self.inventory[item] -= 1
              print(f"Sold 1 unit of {item}")
          else:
              print(f"No stock for {item}")

      def restock(self, item, quantity):
          if item in self.inventory:
              self.inventory[item] += quantity
              print(f"Restocked {quantity} units of {item}")
          else:
              self.inventory[item] = quantity
              print(f"Added {item} with {quantity} units to the inventory")

  inventory = {'apples': 10, 'oranges': 5}
  store = Store(inventory)
  store.sell('apples')
  store.restock('oranges', 10)
  """,

