class SalesDataProcessor {
    private List<int> weeklySales;
    private double discountRate;

    public SalesDataProcessor(List<int> sales, double rate) {
        weeklySales = sales;
        discountRate = rate;
    }

    public double calculateAverageSales() {
        int totalSales = weeklySales.Sum();
        int numWeeks = weeklySales.Count;
        return (double)totalSales / numWeeks;
    }

    public double calculateDiscountedRevenue() {
        double averageSales = calculateAverageSales();
        return averageSales * (1 - discountRate);
    }

    public void applyExtraDiscount(double extra) {
        discountRate += extra;
        if (discountRate > 0.5) {
            discountRate = 0.5;
        }
    }
}",

