class Vehicle:
  def __init__(self, type):
    self.type = type
    self.speed = 0

  def accelerate(self):
    if self.type == 'Car':
      self.speed += 10
    elif self.type == 'Bike':
      self.speed += 5

  def apply_brakes(self):
    if self.speed > 0:
      self.speed -= 5

class TrafficLight:
  def __init__(self):
    self.state = 'Red'

  def change_light(self, new_state):
    self.state = new_state

class Simulation:
  def __init__(self, vehicle, traffic_light):
    self.vehicle = vehicle
    self.traffic_light = traffic_light

  def run(self):
    if self.traffic_light.state == 'Green':
      self.vehicle.accelerate()
    elif self.traffic_light.state == 'Yellow':
      self.vehicle.accelerate()
      self.vehicle.apply_brakes()
    elif self.traffic_light.state == 'Red':
      self.vehicle.apply_brakes()

vehicle = Vehicle('Car')
traffic_light = TrafficLight()
simulation = Simulation(vehicle, traffic_light)
traffic_light.change_light('Green')
simulation.run()
