public class SalesAnalysis {
   private int numberOfSales;
   private double totalRevenue;

   public SalesAnalysis(int sales, double revenue) {
      this.numberOfSales = sales;
      this.totalRevenue = revenue;
   }

   public void updateSales(int quantity) {
      numberOfSales += quantity;
   }

   public void updateRevenue(double amount) {
      if (amount > 0) {
         totalRevenue += amount;
      }
   }

   public double averageSaleValue() {
      return totalRevenue / numberOfSales;
